import 'package:bitcoin_base_i/src/bitcoin/address/address.dart';
import 'package:bitcoin_base_i/src/bitcoin/constant/constant.dart';
import 'package:bitcoin_base_i/src/bitcoin/script/input.dart';
import 'package:bitcoin_base_i/src/bitcoin/script/output.dart';
import 'package:bitcoin_base_i/src/bitcoin/script/script.dart';
import 'package:bitcoin_base_i/src/bitcoin/script/transaction.dart';
import 'package:bitcoin_base_i/src/crypto/ec/ec_private.dart';
import 'package:flutter_test/flutter_test.dart';

void main() {
  group("P2KH", () {
    final txin = TxInput(
        txId:
            'fb48f4e23bf6ddf606714141ac78c3e921c8c0bebeb7c8abb2c799e9ff96ce6c',
        txIndex: 0);
    final addr = P2pkhAddress(address: 'n4bkvTyU1dVdzsrhWBqBw8fEMbHjJvtmJR');
    final txout = TxOutput(
        amount: BigInt.from(10000000),
        scriptPubKey: Script(script: [
          'OP_DUP',
          'OP_HASH160',
          addr.getH160,
          'OP_EQUALVERIFY',
          'OP_CHECKSIG'
        ]));
    final changeAddr =
        P2pkhAddress(address: 'mytmhndz4UbEMeoSZorXXrLpPfeoFUDzEp');
    final changeTxout = TxOutput(
        amount: BigInt.from(29000000),
        scriptPubKey: Script(script: changeAddr.toScriptPubKey()));
    final changeLowSAddr =
        P2pkhAddress(address: 'mmYNBho9BWQB2dSniP1NJvnPoj5EVWw89w');
    final changeLowSTxout = TxOutput(
        amount: BigInt.from(29000000),
        scriptPubKey: Script(script: changeLowSAddr.toScriptPubKey()));
    final sk = ECPrivate.fromWif(
        'cRvyLwCPLU88jsyj94L7iJjQX5C2f8koG4G2gevN4BeSGcEvfKe9');
    final fromAddr =
        P2pkhAddress(address: 'myPAE9HwPeKHh8FjKwBNBaHnemApo3dw6e');

    const String coreTxResult =
        '02000000016cce96ffe999c7b2abc8b7bebec0c821e9c378ac41417106f6ddf63be2f448fb0000000000ffffffff0280969800000000001976a914fd337ad3bf81e086d96a68e1f8d6a0a510f8c24a88ac4081ba01000000001976a914c992931350c9ba48538003706953831402ea34ea88ac00000000';
    const String coreTxSignedResult =
        '';
    const String coreTxSignedLowSSigallResult =
        '';
    const String coreTxSignedLowSSignoneResult =
        '';
    const String coreTxSignedLowSSignoneTxid =
        '105933681b0ca37ae0c0af43ae6f111803c899232b7fd586584b532dbe21ae6f';

    final sigTxin1 = TxInput(
        txId:
            "76464c2b9e2af4d63ef38a77964b3b77e629dddefc5cb9eb1a3645b1608b790f",
        txIndex: 0);
    final sigTxin2 = TxInput(
        txId:
            '76464c2b9e2af4d63ef38a77964b3b77e629dddefc5cb9eb1a3645b1608b790f',
        txIndex: 1);
    final sigFromAddr1 =
        P2pkhAddress(address: 'n4bkvTyU1dVdzsrhWBqBw8fEMbHjJvtmJR');
    final sigFromAddr2 =
        P2pkhAddress(address: 'mmYNBho9BWQB2dSniP1NJvnPoj5EVWw89w');
    final sigSk1 = ECPrivate.fromWif(
        'cTALNpTpRbbxTCJ2A5Vq88UxT44w1PE2cYqiB3n4hRvzyCev1Wwo');
    final sigSk2 = ECPrivate.fromWif(
        'cVf3kGh6552jU2rLaKwXTKq5APHPoZqCP4GQzQirWGHFoHQ9rEVt');
    final sigToAddr1 =
        P2pkhAddress(address: 'myPAE9HwPeKHh8FjKwBNBaHnemApo3dw6e');
    final sigTxout1 = TxOutput(
        amount: BigInt.from(9000000),
        scriptPubKey: Script(script: [
          'OP_DUP',
          'OP_HASH160',
          sigToAddr1.getH160,
          'OP_EQUALVERIFY',
          'OP_CHECKSIG'
        ]));
    final sigToAddr2 =
        P2pkhAddress(address: 'mmYNBho9BWQB2dSniP1NJvnPoj5EVWw89w');
    final sigTxout2 = TxOutput(
        amount: BigInt.from(900000),
        scriptPubKey: Script(script: [
          'OP_DUP',
          'OP_HASH160',
          sigToAddr2.getH160,
          'OP_EQUALVERIFY',
          'OP_CHECKSIG'
        ]));
    const String sigSighashSingleResult =
        '';
    const String signSighashAll2in2outResult =
        '';
    const String signSighashNone2in2outResult =
        '';

    const String signSighashAllSingleAnyone2in2outResult =
        '';

    test("test1", () {
      final tx = BtcTransaction(inputs: [txin], outputs: [txout, changeTxout]);
      expect(tx.serialize(), coreTxResult);
    });
    test("test2", () {
      final tx = BtcTransaction(inputs: [txin], outputs: [txout, changeTxout]);
      final digit = tx.getTransactionDigest(
          txInIndex: 0,
          script: Script(script: [
            'OP_DUP',
            'OP_HASH160',
            fromAddr.getH160,
            'OP_EQUALVERIFY',
            'OP_CHECKSIG'
          ]));
      final sig = sk.signInput(digit);
      txin.scriptSig = Script(script: [sig, sk.getPublic().toHex()]);
      expect(tx.serialize(), coreTxSignedResult);
    });
    test("test3", () {
      final tx =
          BtcTransaction(inputs: [txin], outputs: [txout, changeLowSTxout]);
      final digit = tx.getTransactionDigest(
          txInIndex: 0, script: Script(script: fromAddr.toScriptPubKey()));
      final sig = sk.signInput(digit);
      txin.scriptSig = Script(script: [sig, sk.getPublic().toHex()]);
      expect(tx.serialize(), coreTxSignedLowSSigallResult);
    });
    test("test4", () {
      final tx =
          BtcTransaction(inputs: [txin], outputs: [txout, changeLowSTxout]);
      final digit = tx.getTransactionDigest(
          txInIndex: 0,
          script: Script(script: fromAddr.toScriptPubKey()),
          sighash: SIGHASH_NONE);
      final sig = sk.signInput(digit, sigHash: SIGHASH_NONE);
      txin.scriptSig = Script(script: [sig, sk.getPublic().toHex()]);
      expect(tx.serialize(), coreTxSignedLowSSignoneResult);
      expect(tx.txId(), coreTxSignedLowSSignoneTxid);
    });
    test("test5", () {
      final tx =
          BtcTransaction(inputs: [sigTxin1], outputs: [sigTxout1, sigTxout2]);
      final digit = tx.getTransactionDigest(
          txInIndex: 0,
          script: Script(script: sigFromAddr1.toScriptPubKey()),
          sighash: SIGHASH_SINGLE);
      final sig = sigSk1.signInput(digit, sigHash: SIGHASH_SINGLE);
      sigTxin1.scriptSig = Script(script: [sig, sigSk1.getPublic().toHex()]);
      expect(tx.serialize(), sigSighashSingleResult);
    });
    test("test6", () {
      final tx = BtcTransaction(
          inputs: [sigTxin1, sigTxin2], outputs: [sigTxout1, sigTxout2]);
      final digit = tx.getTransactionDigest(
          txInIndex: 0,
          script: Script(script: sigFromAddr1.toScriptPubKey()),
          sighash: SIGHASH_ALL);
      final sig = sigSk1.signInput(digit, sigHash: SIGHASH_ALL);
      final digit2 = tx.getTransactionDigest(
          txInIndex: 1,
          script: Script(script: sigFromAddr2.toScriptPubKey()),
          sighash: SIGHASH_ALL);
      final sig2 = sigSk2.signInput(digit2, sigHash: SIGHASH_ALL);
      sigTxin1.scriptSig = Script(script: [sig, sigSk1.getPublic().toHex()]);
      sigTxin2.scriptSig = Script(script: [sig2, sigSk2.getPublic().toHex()]);
      expect(tx.serialize(), signSighashAll2in2outResult);
    });
    test("test7", () {
      final tx = BtcTransaction(
          inputs: [sigTxin1, sigTxin2], outputs: [sigTxout1, sigTxout2]);
      final digit = tx.getTransactionDigest(
          txInIndex: 0,
          script: Script(script: sigFromAddr1.toScriptPubKey()),
          sighash: SIGHASH_NONE);
      final sig = sigSk1.signInput(digit, sigHash: SIGHASH_NONE);
      final digit2 = tx.getTransactionDigest(
          txInIndex: 1,
          script: Script(script: sigFromAddr2.toScriptPubKey()),
          sighash: SIGHASH_NONE);
      final sig2 = sigSk2.signInput(digit2, sigHash: SIGHASH_NONE);
      sigTxin1.scriptSig = Script(script: [sig, sigSk1.getPublic().toHex()]);
      sigTxin2.scriptSig = Script(script: [sig2, sigSk2.getPublic().toHex()]);
      expect(tx.serialize(), signSighashNone2in2outResult);
    });
    test("test8", () {
      final tx = BtcTransaction(
          inputs: [sigTxin1, sigTxin2], outputs: [sigTxout1, sigTxout2]);
      final digit = tx.getTransactionDigest(
          txInIndex: 0,
          script: Script(script: sigFromAddr1.toScriptPubKey()),
          sighash: SIGHASH_ALL | SIGHASH_ANYONECANPAY);

      final sig =
          sigSk1.signInput(digit, sigHash: SIGHASH_ALL | SIGHASH_ANYONECANPAY);

      final digit2 = tx.getTransactionDigest(
          txInIndex: 1,
          script: Script(script: sigFromAddr2.toScriptPubKey()),
          sighash: SIGHASH_SINGLE | SIGHASH_ANYONECANPAY);
      final sig2 = sigSk2.signInput(digit2,
          sigHash: SIGHASH_SINGLE | SIGHASH_ANYONECANPAY);
      sigTxin1.scriptSig = Script(script: [sig, sigSk1.getPublic().toHex()]);
      sigTxin2.scriptSig = Script(script: [sig2, sigSk2.getPublic().toHex()]);
      expect(tx.serialize(), signSighashAllSingleAnyone2in2outResult);
    });
  });
}
